    public void kittyTrumpSet()
    {
        if (kitty.hand[0].ToString().Contains("DIAMONDS"))
        {
            kittyTrump = KITTYTRUMP.DIAMONDS;
        }
        else if (kitty.hand[0].ToString().Contains("HEARTS"))
        {
            kittyTrump = KITTYTRUMP.HEARTS;
        }
        else if (kitty.hand[0].ToString().Contains("SPADES"))
        {
            kittyTrump = KITTYTRUMP.SPADES;
        }
        else if (kitty.hand[0].ToString().Contains("CLUBS"))
        {
            kittyTrump = KITTYTRUMP.CLUBS;
        }
    }
    public void getNonTrumpCards()
    {
        for (int i = 0; i < hand.Count; ++i)
        {
            if (kittyTrump != KITTYTRUMP.DIAMONDS && hand[i].ToString().Contains("DIAMONDS"))
            {
                nonMatchingCardsKitty.Add(hand[i]);
            }
            else if (kittyTrump != KITTYTRUMP.HEARTS && hand[i].ToString().Contains("HEARTS"))
            {
                nonMatchingCardsKitty.Add(hand[i]);
            }
            else if (kittyTrump != KITTYTRUMP.SPADES && hand[i].ToString().Contains("SPADES"))
            {
                nonMatchingCardsKitty.Add(hand[i]);
            }
            else if (kittyTrump != KITTYTRUMP.CLUBS && hand[i].ToString().Contains("CLUBS"))
            {
                nonMatchingCardsKitty.Add(hand[i]);
            }
        }
        for(int i = 0; i < nonMatchingCardsKitty.Count; ++i)
        {
            if(kittyTrump == KITTYTRUMP.DIAMONDS && nonMatchingCardsKitty[i].ToString().Contains("JACK_HEARTS"))
            {
                nonMatchingCardsKitty.RemoveAt(i);
            }
            if(kittyTrump == KITTYTRUMP.HEARTS && nonMatchingCardsKitty[i].ToString().Contains("JACK_DIAMONDS"))
            {
                nonMatchingCardsKitty.RemoveAt(i);
            }
            if(kittyTrump == KITTYTRUMP.SPADES && nonMatchingCardsKitty[i].ToString().Contains("JACK_CLUBS"))
            {
                nonMatchingCardsKitty.RemoveAt(i);
            }
            if (kittyTrump == KITTYTRUMP.CLUBS && nonMatchingCardsKitty[i].ToString().Contains("JACK_SPADES"))
            {
                nonMatchingCardsKitty.RemoveAt(i);
            }
        }
    }
