            foundCardToPlay = true;
        }

        if (turn.trumpMadeCounter > 3)
        {
            foundFirstPlayed = true;
        }

        int suitToFollow = 0;

        if (toFollowString.ToString().Contains("DIAMOND") && diamondCardsToPlay.Count > 0)
        {
            handHasSameSuit = true;
            suitToFollow = 1;
        }
        else if (toFollowString.ToString().Contains("HEART") && heartCardsToPlay.Count > 0)
