            handHasSameSuit = true;
            suitToFollow = 2;
        }
        else if (toFollowString.ToString().Contains("SPADE") && spadeCardsToPlay.Count > 0)
        {
            handHasSameSuit = true;
            suitToFollow = 3;
        }
        else if (toFollowString.ToString().Contains("CLUB") && clubCardsToPlay.Count > 0)
        {
            handHasSameSuit = true;
            suitToFollow = 4;
        }
        else
        {
            handHasSameSuit = false;
        }

        if(turn.trumpMadeCounter == 1 && foundCardToPlay == false && trumpCards.Count > 0)
        {
            currentPlayingCard = trumpCards[trumpCards.Count - 1];
            foundCardToPlay = true;
        }

        if (handHasSameSuit == true)
        {
            if(suitToFollow == 1 && foundCardToPlay == false)
            {
                currentPlayingCard = diamondCardsToPlay[diamondCardsToPlay.Count - 1];
                foundCardToPlay = true;
            }
            if (suitToFollow == 2 && foundCardToPlay == false)
            {
                currentPlayingCard = heartCardsToPlay[heartCardsToPlay.Count - 1];
                foundCardToPlay = true;
            }
            if (suitToFollow == 3 && foundCardToPlay == false)
            {
                currentPlayingCard = spadeCardsToPlay[spadeCardsToPlay.Count - 1];
                foundCardToPlay = true;
            }
            if (suitToFollow == 4 && foundCardToPlay == false)
            {
                currentPlayingCard = clubCardsToPlay[clubCardsToPlay.Count - 1];
                foundCardToPlay = true;
            }

        }
        else if (handHasSameSuit == false)
        {
            if (trumpCards.Count > 0 && foundCardToPlay == false)
            {
                currentPlayingCard = trumpCards[trumpCards.Count - 1];
                foundCardToPlay = true;
            }
            else if (foundCardToPlay == false)
