    public void playCards()
    {
        dir = new Vector3(0.0f, 0.0f, 0.0f);

        if (turn.playerTurn == true && trump.trumpMade == true)
        {
            if (GameObject.FindGameObjectWithTag("Selected") != null)
            {
                playerSpeed = 5.0f;
                cardIsActive = true;
                currentPlayingCardObjPlayer = GameObject.FindGameObjectWithTag("Selected");
                cardMoveLocation.transform.position = new Vector3(0.0f, -0.75f, 0.0f);
