                dir = dir.normalized;
				if(Mathf.Abs(cardMoveLocation.transform.position.magnitude - currentPlayingCardObjPlayer.transform.position.magnitude) < 0.05f)
				{
					playerSpeed = 0.0f;
				}
                currentPlayingCardObjPlayer.transform.position += dir * Time.deltaTime * playerSpeed;
