            if (hand[i] == Deck.Card.ACE_DIAMONDS || hand[i] == Deck.Card.KING_DIAMONDS || hand[i] == Deck.Card.QUEEN_DIAMONDS || hand[i] == Deck.Card.JACK_DIAMONDS
                || hand[i] == Deck.Card.TEN_DIAMONDS || hand[i] == Deck.Card.NINE_DIAMONDS)
