                || hand[i] == Deck.Card.TEN_CLUBS || hand[i] == Deck.Card.NINE_CLUBS)
            {
                canClubs = true;
            }

            if (hand[i] == Deck.Card.ACE_SPADES || hand[i] == Deck.Card.KING_SPADES || hand[i] == Deck.Card.QUEEN_SPADES || hand[i] == Deck.Card.JACK_SPADES
                || hand[i] == Deck.Card.TEN_SPADES || hand[i] == Deck.Card.NINE_SPADES)
            {
                canSpades = true;
            }
        }
    }
