    public void genCards()
    {
        newPositionForCard = new Vector3(0.0f, 0.0f, 0.0f);

        for (int i = 0; i < hand.Count; ++i)
        {
            if (handPositionForGen == HANDPOSITION.ONE)
            {
                if (hand.Count == 5)
                {
                    newPositionForCard.x = -300.0f + 150.0f * i;
                    newPositionForCard.y = -180.0f;
                    newPositionForCard.z = 2.0f * i;
                }
                else if(hand.Count == 4)
                {
                    newPositionForCard.x = -200.0f + 150.0f * i;
                    newPositionForCard.y = -180.0f;
                    newPositionForCard.z = 2.0f * i;
                }
                else if(hand.Count == 3)
                {
                    newPositionForCard.x = -150.0f + 150.0f * i;
                    newPositionForCard.y = -180.0f;
                    newPositionForCard.z = 2.0f * i;
                }
                else if(hand.Count == 2)
                {
                    newPositionForCard.x = -100.0f + 150.0f * i;
                    newPositionForCard.y = -180.0f;
                    newPositionForCard.z = 2.0f * i;
                }
                else if(hand.Count == 1)
                {
                    newPositionForCard.x = 0.0f;
                    newPositionForCard.y = -180.0f;
                    newPositionForCard.z = 2.0f * i;
                }
                playerSpawnLocation.transform.position = newPositionForCard;

                //cardObjects[i].transform.localPosition = new Vector3(newPositionForCard.x, newPositionForCard.y, newPositionForCard.z);
                cardsToMove[i].transform.localPosition = playerSpawnLocation.transform.position;
            }
            if (handPositionForGen == HANDPOSITION.TWO)
            {
                newPositionForCard.x = -750.0f;
                newPositionForCard.y = 250.0f - 125.0f * i;
                newPositionForCard.z = 2.0f * i;
                //GameObject newCard = Instantiate(cardObjects[i], position, Quaternion.identity);
                //newCard.name = cardObjects[i].ToString();
				cardsToMove[i].transform.localPosition = newPositionForCard;
            }
            if (handPositionForGen == HANDPOSITION.THREE)
            {
                newPositionForCard.x = -300.0f + 150.0f * i;
                newPositionForCard.y = 750.0f;
                newPositionForCard.z = 2.0f * i;
                //GameObject newCard = Instantiate(cardObjects[i], position, Quaternion.identity);
                //newCard.name = cardObjects[i].ToString();
				cardsToMove[i].transform.localPosition = newPositionForCard;
            }
            if (handPositionForGen == HANDPOSITION.FOUR)
            {
                newPositionForCard.x = 750.0f;
                newPositionForCard.y = 250.0f - 125.0f * i;
                newPositionForCard.z = 2.0f * i;
                //GameObject newCard = Instantiate(cardObjects[i], position, Quaternion.identity);
                //newCard.name = cardObjects[i].ToString();
				cardsToMove[i].transform.localPosition = newPositionForCard;
            }
        }
        if (handPositionForGen == HANDPOSITION.FIVE)
        {
            for (int i = 0; i < 3; ++i)
            {
                newPositionForCard.x = -1000.0f;
                cardsToMove[i].transform.localPosition = newPositionForCard;
            }
        }
    }
